package Day3;

public class Cl_loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1.  while loop
		int i = 1; // while loop initiation is needed
		while ( i < 10) // the loop statements execute as long the condition is true. SO the print max will be 9.
		{
			System.out.println(i);
			i++;  // incremental statement as the last line
		}
		System.out.println("******");
		//2. For Loop
		for (int j =1; j< 10; j++)
		{
			System.out.println(j);
		}
		System.out.println("******");
		//3. do while loop
		
		
	}

}
